CREATE PROCEDURE [StriveCarSalon].[uspDeleteWashes]
(
@JobId int
)
AS

BEGIN
SELECT ServiceId ,ServiceName ,ServiceType ,LocationId ,Cost ,Commision ,CommisionType ,Upcharges ,ParentServiceId ,CommissionCost ,IsActive ,IsDeleted ,CreatedBy ,CreatedDate ,UpdatedBy ,UpdatedDate FROM tblService WHERE IsActive=1 AND ISNULL(IsDeleted,0)=0;
SELECT ProductId ,ProductName ,ProductCode ,ProductDescription ,ProductType ,FileName ,ThumbFileName ,LocationId ,VendorId ,Size ,SizeDescription ,Quantity ,QuantityDescription ,Cost ,Price ,IsTaxable ,TaxAmount ,ThresholdLimit ,IsActive ,IsDeleted ,CreatedBy ,CreatedDate ,UpdatedBy ,UpdatedDate FROM tblProduct WHERE IsActive=1 AND ISNULL(IsDeleted,0)=0;
END

BEGIN
SELECT BayId,BayName FROM tblBay WHERE LocationId=@LocationId AND IsActive=1 AND IsDeleted = 0;
SELECT tblB.BayId ,tblB.JobId ,SUBSTRING(CONVERT(VARCHAR(8),ScheduleInTime,108),0,6) AS ScheduleInTime FROM tblBaySchedule tblB INNER JOIN tblBay tblBa ON(tblB.BayId=tblBa.BayId) WHERE tblB.ScheduleDate=@JobDate AND tblB.IsActive=1 AND tblBa.IsActive=1 AND ISNULL(tblB.IsDeleted,0)=0 AND ISNULL(tblBa.IsDeleted,0)=0 AND tblBa.LocationId=@LocationId;
END

