CREATE PROC [StriveCarSalon].[uspGetSchedule] 
@LocationId int,
@ScheduledStartDate Date = NULL,
@ScheduledEndDate Date = NULL
AS

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
SELECT TimeClockId, EmployeeId, LocationId, RoleId, EventDate, InTime, OutTime, EventType, UpdatedFrom, [Status] FROM StriveCarSalon.tblTimeClock WHERE LocationId = @LocationId and EmployeeId = @EmployeeId AND RoleId = @RoleId AND CONVERT(date, EventDate) = @Date AND (IsDeleted = 0 OR IsDeleted is null) AND IsActive = 1
END

